import React from "react";
import { View, ImageBackground } from "react-native";
import { makeStyles, Button, Image } from "@rneui/themed";
import Icon from "@mdi/react";
import { mdiFacebook, mdiGoogle } from "@mdi/js";

export default function App() {
  const styles = useStyles();

  return (
    <ImageBackground
      source={require("../assets/imgs/fondoLogin3.png")}
      resizeMode="cover"
      style={styles.image}
    >
      <View style={styles.container}>
        <Image
          source={require("../assets/imgs/logo2.png")}
          resizeMode="cover"
          style={{ width: 300, height: 220, marginBottom: 50 }}
        ></Image>
        <View>
          <Button
            titleStyle={{ fontWeight: "700", color: "black" }}
            buttonStyle={{
              backgroundColor: "white",
              borderColor: "black",
              borderWidth: 1,
              borderRadius: 30,
            }}
            containerStyle={{
              width: "100%",
              marginVertical: 10,
            }}
          >
            <Icon
              path={mdiGoogle}
              title="google"
              size={1}
              color="red"
              style={{ marginRight: 5 }}
            />
            CONTINUAR CON GOOGLE
          </Button>
          <Button
            titleStyle={{ fontWeight: "700", color: "black" }}
            buttonStyle={{
              backgroundColor: "#3b5998",
              borderColor: "black",
              borderWidth: 1,
              borderRadius: 30,
            }}
            containerStyle={{
              width: "100%",
              marginVertical: 10,
            }}
          >
            <Icon
              path={mdiFacebook}
              title="google"
              size={1}
              color="white"
              style={{ marginRight: 5 }}
            />
          </Button>
          <Button
            titleStyle={{ fontWeight: "700", color: "black" }}
            buttonStyle={{
              backgroundColor: "white",
              borderColor: "black",
              borderWidth: 1,
              borderRadius: 30,
            }}
            containerStyle={{
              width: "100%",
              marginVertical: 10,
            }}
          ></Button>
        </View>
      </View>
    </ImageBackground>
  );
}

const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: theme.colors.white,
    padding: "50px",
    width: "50%",
    height: "70%",
    borderRadius: "40px",
    boxShadow: "2px 2px 2px",
  },
  image: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    width: "100%",
    height: "100%",
  },
  text: {
    marginVertical: theme.spacing.lg,
  },
  overlayView: {
    height: "100%",
    width: "100%",
    position: "absolute",
    backgroundColor: "rgba(0, 204, 0, 0.5)",
  },
  buttonLoggin: {
    backgroundColor: "rgba(90, 154, 230, 1)",
    borderColor: "black",
    borderWidth: 1,
    borderRadius: 30,
  },
  iconButtonLogin: {
    marginRight: 10,
  },
}));
