import React from "react";
import { View, ImageBackground } from "react-native";
import { makeStyles, Text, Button, Image, Icon } from "@rneui/themed";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'

export default function App() {
  const styles = useStyles();

  return (
    <ImageBackground
      source={require("../assets/imgs/fondoLogin3.png")}
      resizeMode="cover"
      style={styles.image}
    >
      <View style={styles.container}>
        <Image
          source={require("../assets/imgs/logo1.png")}
          style={{ width: 200, height: 200 }}
        ></Image>
        <View>
          <Button
            titleStyle={{ fontWeight: "700", color: "black" }}
            buttonStyle={{
              backgroundColor: "white",
              borderColor: "black",
              borderWidth: 1,
              borderRadius: 30,
            }}
            containerStyle={{
              width: "100%",
              marginVertical: 10,
            }}
          >
            <FontAwesomeIcon icon="square-check" />
            CONTINUAR CON GOOGLE
          </Button>
          <Button
            titleStyle={{ fontWeight: "700", color: "black" }}
            buttonStyle={{
              backgroundColor: "white",
              borderColor: "black",
              borderWidth: 1,
              borderRadius: 30,
            }}
            containerStyle={{
              width: "100%",
              marginVertical: 10,
            }}
          >
            <FontAwesomeIcon icon="square-check" />
            CONTINUAR CON FACEBOOK
          </Button>
          <Button
            titleStyle={{ fontWeight: "700", color: "black" }}
            buttonStyle={{
              backgroundColor: "white",
              borderColor: "black",
              borderWidth: 1,
              borderRadius: 30,
            }}
            containerStyle={{
              width: "100%",
              marginVertical: 10,
            }}
          >
            <FontAwesomeIcon icon="square-check" />
            CONTINUAR CON APPLE
          </Button>
        </View>
        <Text h3>Start Using RNE </Text>
        <Text style={styles.text}>
          Open up App.tsx to start working on your apps!
        </Text>
        <Button>Switch Theme</Button>
      </View>
    </ImageBackground>
  );
}

const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: theme.colors.white,
    padding: "50px",
    width: "70%",
    height: "60%",
    borderRadius: "40px",
    boxShadow: "2px 2px 2px",
  },
  image: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    width: "100%",
    height: "100%",
  },
  text: {
    marginVertical: theme.spacing.lg,
  },
  overlayView: {
    height: "100%",
    width: "100%",
    position: "absolute",
    backgroundColor: "rgba(0, 204, 0, 0.5)",
  },
  buttonLoggin: {
    backgroundColor: "rgba(90, 154, 230, 1)",
    borderColor: "black",
    borderWidth: 1,
    borderRadius: 30,
  },
}));
