import React from "react";
import { View, ImageBackground } from "react-native";
import { makeStyles, Text, Button} from "@rneui/themed";

export default function App() {
  const styles = useStyles();

  return (
    <ImageBackground source={require('../assets/imgs/fondoLogin2.jpg')} resizeMode="cover" style={styles.image}>
      <View style={styles.container}>
        <Text h3>Start Using RNE </Text>
        <Text style={styles.text}>
          Open up App.tsx to start working on your apps!
        </Text>
        <Button>Switch Theme</Button>
      </View>
    </ImageBackground>
  );
}

const useStyles = makeStyles((theme) => ({
  container: {
    display:"flex",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: theme.colors.white,
    padding: "50px",
    width:"50%",
    height:"50%",
    borderRadius: "40px",
    boxShadow: "2px 2px",
  },
  image: {
    display: "flex",
    justifyContent: "center",
    alignItems:"center",
    width:"100%",
    height:"100%",
  },
  text: {
    marginVertical: theme.spacing.lg,
  },
  overlayView: {
    height: "100%",
    width: "100%",
    position: 'absolute',
    backgroundColor: 'rgba(0, 204, 0, 0.5)',

}
}));
